import React from 'react'
import Badge from '@mui/material/Badge';
import NotificationsIcon from '@mui/icons-material/Notifications';
import IconButton  from '@mui/material/IconButton';
import Tooltip from '@mui/material/Tooltip';
import BasicMenu from '../../Common/BasicMenu/BasicMenu';

const NotificationBell = ({iconColor, badgeContent, onClick, anchorEl}) => {
    const newNotification = `You have ${badgeContent} new notifications`
    const noNotifications = 'No new notifications'
    return(
       <div>
         <Tooltip  title={badgeContent ? newNotification : noNotifications} >
            <IconButton
                color={iconColor}
                onClick={handleOpen}
                anchorEl={anchorEl}
        >
             <Badge 
                badgeContent={badgeContent} 
                color ="error">
                <NotificationsIcon/>
            </Badge>
            </IconButton>
        </Tooltip>
        <BasicMenu
            open={open} 
            anchorEl={anchorEl}
            handleClose={handleClose}
         />
      </div>
    )
}

export default NotificationBell
